import React, { useState } from 'react'
import assets from '../../assets';
import axios from 'axios';
import './LoginSignUp.css'
type Props = {}

const LoginSignUp = (props: Props) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const handleLogin = async () => {
        try {
            console.log(username, password);	
          const response = await axios.post('/login', { username, password });
          const isAdmin = response.data.isAdmin;
          if (isAdmin) {
            // Redirect to admin dashboard
            window.location.href = '/Admin';
          } else {
            window.location.href = '/';
          }
        } catch (error) {
          console.error('Login failed:', error);
          // Handle login failure
        }
      };  
      const handleLoginClick = () => {
        if (action === 'Log In') {
          handleLogin();
        } else {
          setAction('Log In');
        }
      };
    const [action,setAction] =useState("Sign Up");
  return (

        <div >
            <div className='container'>
            <div className="header">
                <div className="text"> {action} </div>
                <div className="underline"></div>
            </div>
            <div className="inputs">


                {action==="Log In"?<div></div> :<div className="input">
                    <img src={assets.image.person} alt =""/>
                    <input type="Full Name" placeholder='First Name' />
                </div>}
                <div className="input">
                    <img src={assets.image.email} alt =""/>
                    <input type="email" placeholder='Email' value={username} onChange={(e) => setUsername(e.target.value)}/>
                </div>
                <div className="input">
                    <img src={assets.image.password} alt =""/>
                    <input type="password" placeholder='Password' value={password} onChange={(e) => setPassword(e.target.value)}/>
                </div>
            </div>

            {action==="Sign Up"?<div></div> :<div className="forgot-password" >Lost Password ? <span>Click Here !</span></div>}
        </div>
        <div className="submit-container">
            <div className={action==="Sign Up"?"submit":"submit gray"} onClick={()=>{setAction("Sign Up")}} >Sign Up</div>
            <div className={action==="Sign Up"?"submit gray":"submit"} onClick={handleLoginClick }>Log in</div>
        </div>
        </div>


  )
}

export default LoginSignUp
