import React, { useState } from 'react';
import assets from '../../assets';
import axios from 'axios';
import './LoginSignUp.css';

const LoginSignUp = () => {
    const [action, setAction] = useState('Log In');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [fullName, setFullName] = useState('');

    const handleLogin = async () => {
        try {
            console.log(username, password);
            const response = await axios.post('/login', { username, password });
            const isAdmin = response.data.isAdmin;
            if (isAdmin) {
                // Redirect to admin dashboard
                window.location.href = '/Admin';
            } else {
                window.location.href = '/';
            }
        } catch (error) {
            console.error('Login failed:', error);
            // Handle login failure
        }
    };

    const handleRegister = async () => {
        try {
            console.log(fullName, username, password);
            const response = await axios.post('/register', { fullName, username, password });
            // Handle registration success
        } catch (error) {
            console.error('Registration failed:', error);
            // Handle registration failure
        }
    };

    const handleActionChange = () => {
        if (action === 'Log In') {
            setAction('Sign Up');
        } else {
            setAction('Log In');
        }
    };

    return (
        <div className='container'>
            <div className='header'>
                <div className='text'>{action}</div>
                <div className='underline'></div>
            </div>
            <div className='inputs'>
                {action === 'Sign Up' && (
                    <div className='input'>
                        <img src={assets.image.person} alt='' />
                        <input
                            type='text'
                            placeholder='Full Name'
                            value={fullName}
                            onChange={(e) => setFullName(e.target.value)}
                        />
                    </div>
                )}
                <div className='input'>
                    <img src={assets.image.email} alt='' />
                    <input
                        type='email'
                        placeholder='Email'
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>
                <div className='input'>
                    <img src={assets.image.password} alt='' />
                    <input
                        type='password'
                        placeholder='Password'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
            </div>
            <div className='submit-container'>
                <div className={action === 'Sign Up' ? 'submit' : 'submit gray'} onClick={handleRegister}>
                    Sign Up
                </div>
                <div className={action === 'Sign Up' ? 'submit gray' : 'submit'} onClick={handleLogin}>
                    Log in
                </div>
            </div>
            <div className='action' onClick={handleActionChange}>
                {action === 'Sign Up' ? 'Log In' : 'Sign Up'}
            </div>
        </div>
    );
};

export default LoginSignUp;
